sys_script_client.script:
//el presente client script sirve para ocultar las secciones del formulario de purchase requisition dependiendo del valor del campo "expense type".

function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }


	//si expense type = capex

	if(g_form.getValue('expense_type') == 'Capex')
	{
		g_form.setSectionDisplay("capex_documenttracking", true);
		g_form.setSectionDisplay("opex_documenttracking", false);
		g_form.setSectionDisplay("capex_aginginformation", true);
		g_form.setSectionDisplay("opex_aginginformation", false);

	}

	//si expense type = opex

	if(g_form.getValue('expense_type') == 'Opex')
	{
		g_form.setSectionDisplay("capex_documenttracking", false);
		g_form.setSectionDisplay("opex_documenttracking", true);
		g_form.setSectionDisplay("capex_aginginformation", false);
		g_form.setSectionDisplay("opex_aginginformation", true);

	}
}
