sys_script_client.script:
function onLoad() {


	//primeramente el campo "expense type" debe de encontrarse como solo visible
	g_form.setReadOnly('expense_type', true);

	//las secciones "capex document tracking" y "opex document tracking" deben de estar ocultas
	g_form.setSectionDisplay("capex_documenttracking", false);
	g_form.setSectionDisplay("opex_documenttracking", false); 

	//el campo "state" debe de encontrarse como read only
	g_form.setReadOnly('state', true);

	//se ocultan los campos "assignment group" y "assigned to"
	g_form.setVisible('assignment_group', false);
	g_form.setVisible('assigned_to', false);

}
