sys_script_client.script:
function onSubmit() {

	if(g_form.getValue('request_type') != 'Capacity problem')
	{

		var solution_document_url = g_form.getValue('solution_document_url');
		var state = g_form.getValue('state');


		if(solution_document_url == '<Insert your Ajax URL or TSpace URL where specific files for this request can be found>' && (state == 7 || state == 6))
		{

			alert("You need to specify a valid URL of the documents to attach.");
			g_form.hideFieldMsg('solution_document_url', true);
			g_form.showErrorBox("solution_document_url", "You need to specify a valid URL of the documents to attach.");

			return false;

		}

	}
}
