sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var sched = new GlideSchedule('730837a5db2a4b00e7bbdbbb5e96194d');


	//calculo de la duración de la entrega del OP
	var op_request_date_glide = new GlideDateTime(current.op_request_date);
	var op_delivery_date_glide = new GlideDateTime(current.op_delivery_date);

	var op_dur = sched.duration(op_request_date_glide, op_delivery_date_glide);
	current.op_requested_to_delivered_duration = op_dur.getDurationValue();


	//calculo de la duración de la entrega del PR
	var pr_request_date_glide = new GlideDateTime(current.pr_request_date);
	var pr_delivery_date_glide = new GlideDateTime(current.pr_delivery_date);

	var pr_dur = sched.duration(pr_request_date_glide, pr_delivery_date_glide);
	current.pr_requested_to_delivered_duration = pr_dur.getDurationValue();


    //calculo de la duración de la entrega del RFQ
	var rfq_request_date_glide = new GlideDateTime(current.rfq_request_date);
	var rfq_delivery_date_glide = new GlideDateTime(current.rfq_delivery_date);

	var rfq_dur = sched.duration(rfq_request_date_glide, rfq_delivery_date_glide);
	current.rfq_requested_to_delivered_duration = rfq_dur.getDurationValue();


	//calculo de la duración de la entrega del SOCA
	var soca_request_date_glide = new GlideDateTime(current.soca_request_date);
	var soca_delivery_date_glide = new GlideDateTime(current.soca_delivery_date);

	var soca_dur = sched.duration(soca_request_date_glide, soca_delivery_date_glide);
	current.soca_requested_to_delivered_duration = soca_dur.getDurationValue();


	//calculo de la duración de la entrega del OCA
	var oca_request_date_glide = new GlideDateTime(current.oca_request_date);
	var oca_delivery_date_glide = new GlideDateTime(current.oca_delivery_date);

	var oca_dur = sched.duration(oca_request_date_glide, oca_delivery_date_glide);
	current.oca_requested_to_delivered_duration = oca_dur.getDurationValue();




})(current, previous);
