sys_ui_policy.script_false:
function onCondition() {

	g_form.setSectionDisplay("capex_documenttracking", false);
	g_form.setSectionDisplay("opex_documenttracking", false);
	g_form.setSectionDisplay("capex_trackingstateinformation", false);
	g_form.setSectionDisplay("opex_trackingstateinformation", false);
}

sys_ui_policy.script_true:
function onCondition() {

	if(g_form.getValue('expense_type') == 'Capex')
	{
		g_form.setSectionDisplay("capex_documenttracking", true);
		g_form.setSectionDisplay("opex_documenttracking", false);
		g_form.setSectionDisplay("capex_trackingstateinformation", true);
		g_form.setSectionDisplay("opex_trackingstateinformation", false);

	}

	if(g_form.getValue('expense_type') == 'Opex')
	{
		g_form.setSectionDisplay("capex_documenttracking", false);
		g_form.setSectionDisplay("opex_documenttracking", true);
		g_form.setSectionDisplay("capex_trackingstateinformation", false);
		g_form.setSectionDisplay("opex_trackingstateinformation", true);

	}

}
