sys_ui_policy.script_true:
function onCondition() {

	//el presente script controla para las compras CAPEX que ordenes de compra son necesarias como obligatorias a partir de lo definido en el campo "item classification". En caso de necesitar modificaciones tambiÃ©n considerar la business rule "PRQ mandatory fields on finished (Capex)" 

    var item_classification_array = g_form.getValue('item_classification').split(',');

	for(var i=0; i<item_classification_array.length; i++)
		{

			if(item_classification_array[i] == g_scratchpad.item_class_software)
			{
			    g_form.setMandatory('po_software', true);
		    }

		    if(item_classification_array[i] == g_scratchpad.item_class_hardware)
			{
			    g_form.setMandatory('po_hardware', true);
		    }


		    if(item_classification_array[i] == g_scratchpad.item_class_installation_service)
			{
			    g_form.setMandatory('po_installation_service', true);
		    }


		    if(item_classification_array[i] == g_scratchpad.item_class_configuration_integration_service)
			{
			    g_form.setMandatory('po_configuration_integration_service', true);
		    }


		if(item_classification_array[i] == g_scratchpad.item_class_other)
			{
			    g_form.setMandatory('po_others', true);
		    }
	}


}
