sys_variable_value.value:
//Por medio de este script se define si se tuvo un retraso, esto depende si el campo OP se encuentra como nulo

if(current.net_id.nil())
{
	current.delayed = true;
	current.delayed_document = 'OP';
	current.delayed_op = true;
	current.update();
}

//Por medio de este script se define si se tuvo un retraso, esto depende si todos los campos de PO's se encuentran como nulos

if(current.soca.nil() && current.oca.nil())
{
	current.delayed = true;
	current.delayed_document = 'SOCA/OCA';
	current.delayed_soca_oca = true;
	current.update();
}

//Por medio de este script se define si se tuvo un retraso, esto depende si el campo PR se encuentra como nulo

if(current.pr.nil())
{
	current.delayed = true;
	current.delayed_document = 'PR';
	current.delayed_pr = true;
	current.update();
}

//Por medio de este script se define si se tuvo un retraso, esto depende si el campo RFQ se encuentra como nulo

if(current.rfq.nil())
{
	current.delayed = true;
	current.delayed_document = 'RFQ';
	current.delayed_rfq = true;
	current.update();
}

// Set 'answer' to the number of seconds this timer should wait
var seconds_to_wait = global.getPurchaseDocumentTimeToWait('Opex', 'OP');
seconds_to_wait = parseInt(seconds_to_wait);
answer = seconds_to_wait;

// Set 'answer' to the number of seconds this timer should wait
var seconds_to_wait = global.getPurchaseDocumentTimeToWait('Opex', 'SOCA/OCA');
answer = seconds_to_wait;

// Set 'answer' to the number of seconds this timer should wait
var seconds_to_wait = global.getPurchaseDocumentTimeToWait('Opex', 'PR');
answer = seconds_to_wait;

// Set 'answer' to the number of seconds this timer should wait
var seconds_to_wait = global.getPurchaseDocumentTimeToWait('Opex', 'RFQ');
answer = seconds_to_wait;
