// Set the variable 'answer' to a comma-separated list of user ids and/or group ids or an array of user/group ids to add as approvers.
//
// For example:
//       answer = [];
//       answer.push('id1');
//       answer.push('id2');

var answer = [];

var prq = new GlideRecord('x_attme_engr_strat_purchase_requisition');
prq.addQuery('sys_id', current.purchase_requisition);
prq.query();

if(prq.next())
{
	answer.push(prq.approver);
}

// Set the variable 'answer' to '', 'approved' or 'rejected' to indicate the approval status for this approval.  When called, the following information is available:
//
//    counts.total = total number of users that are part of this approval
//    counts.approved = # of users that approved so far
//    counts.rejected = # of users that rejected so far
//    counts.requested = # of users that are pending approval
//    counts.not_requested = # of users that are not pending approval
//    counts.not_required = # of users that approval is not required
//

// por medio de este script se busca al director del proyecto al cual se encuentra relacionado la purchase requisition relacionada con la solicitud de despacho.

var answer = [];
var project_sys_id;
var project_responsible;
var project_director;

//primeramente se busca la purchase requisition relacionada con la dispatch request
var prq = new GlideRecord('x_attme_engr_strat_purchase_requisition');
prq.addQuery('sys_id', current.purchase_requisition);
prq.query();

if(prq.next())
{
	project_sys_id = prq.project;
}

//ahora se busca al directo del proyecto
var project = new GlideRecord('x_attme_engr_strat_project');
project.addQuery('sys_id', project_sys_id);
project.query();

if(project.next())
{
	project_responsible = project.project_responsible;
    project_director = project.project_director;	
}

//luego se verifica que el director del proyecto sea diferente del gerente o responsable del proyecto

if(project_director != project_responsible)
{
	answer.push(project_director);
}

//en el caso de que sean el mismo no se define un autorizador y por tanto esta actividad deriva en una auto-aprobacin, dicho escenario puede ser correcto porque el gerente/responsable del proyecto puede ser el mismo que el director.
