sys_variable_value.value:
//Por medio de este script se define si se tuvo un retraso, esto depende si el campo NET ID se encuentra como nulo

if(current.net_id.nil())
{
	current.delayed = true;
	current.delayed_document = 'NET ID';
	current.delayed_net_id = true;
	current.update();
}

//Por medio de este script se define si se tuvo un retraso, esto depende si todos los campos de PO's se encuentran como nulos

if(current.po_software.nil() && current.po_hardware.nil() && current.po_installation_service.nil() && current.po_configuration_integration_service.nil() && current.po_others.nil())
{
	current.delayed = true;
	current.delayed_document = 'PO';
	current.delayed_po = true;
	current.update();
}

//Por medio de este script se define si se tuvo un retraso, esto depende si el campo PR se encuentra como nulo

if(current.pr.nil())
{
	current.delayed = true;
	current.delayed_document = 'PR';
	current.delayed_pr = true;
	current.update();
}

//Por medio de este script se define si se tuvo un retraso, esto depende si el campo Requisition se encuentra como nulo

if(current.requisition.nil())
{
	current.delayed = true;
	current.delayed_document = 'Requisition';
	current.delayed_requisition = true;
	current.update();
}

//Por medio de este script se define si se tuvo un retraso, esto depende si el campo RFQ se encuentra como nulo

if(current.rfq.nil())
{
	current.delayed = true;
	current.delayed_document = 'RFQ';
	current.delayed_rfq = true;
	current.update();
}

// Set 'answer' to the number of seconds this timer should wait
var seconds_to_wait = global.getPurchaseDocumentTimeToWait('Capex', 'NET ID');
seconds_to_wait = parseInt(seconds_to_wait);
answer = seconds_to_wait;

// Set 'answer' to the number of seconds this timer should wait
var seconds_to_wait = global.getPurchaseDocumentTimeToWait('Capex', 'PO');
answer = seconds_to_wait;

// Set 'answer' to the number of seconds this timer should wait
var seconds_to_wait = global.getPurchaseDocumentTimeToWait('Capex', 'PR');
answer = seconds_to_wait;

// Set 'answer' to the number of seconds this timer should wait
var seconds_to_wait = global.getPurchaseDocumentTimeToWait('Capex', 'Requisition');
answer = seconds_to_wait;

// Set 'answer' to the number of seconds this timer should wait
var seconds_to_wait = global.getPurchaseDocumentTimeToWait('Capex', 'RFQ');
answer = seconds_to_wait;
